contracts/DisputeResolution.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title DisputeResolution
 * @author AI Dispute Resolution Team
 * @notice Main contract for AI-verified dispute resolution on Base L2
 * @dev Integrates with GenLayer for AI consensus verdicts
 */
contract DisputeResolution is ReentrancyGuard, AccessControl, Pausable {

    // ============ Roles ============
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant ARBITER_ROLE = keccak256("ARBITER_ROLE");

    // ============ Enums ============
    enum DisputeStatus {
        Created,
        EvidenceSubmission,
        AwaitingAIVerdict,
        VerdictDelivered,
        AppealPeriod,
        Resolved,
        Cancelled
    }

    enum Resolution {
        None,
        FavorClaimant,
        FavorRespondent,
        Split,
        Dismissed
    }

    enum DisputeCategory {
        ContractBreach,
        ServiceQuality,
        PaymentDispute,
        IntellectualProperty,
        FraudClaim,
        Other
    }

    // ============ Structs ============
    struct Dispute {
        uint256 id;
        address claimant;
        address respondent;
        uint256 amount;
        uint256 createdAt;
        uint256 evidenceDeadline;
        uint256 appealDeadline;
        DisputeStatus status;
        Resolution resolution;
        DisputeCategory category;
        string descriptionHash;  // IPFS hash
        uint8 aiConfidenceScore; // 0-100
        bool appealed;
    }

    struct Evidence {
        address submitter;
        string contentHash;     // IPFS hash
        uint256 timestamp;
        EvidenceType evidenceType;
    }

    enum EvidenceType {
        Document,
        Image,
        Video,
        Contract,
        Communication,
        Transaction,
        Other
    }

    struct AIVerdict {
        Resolution decision;
        uint8 confidenceScore;
        string reasoningHash;   // IPFS hash for detailed reasoning
        uint256 timestamp;
        bytes32 genLayerRequestId;
    }

    // ============ State Variables ============
    uint256 public disputeCounter;
    uint256 public constant MIN_DISPUTE_AMOUNT = 0.001 ether;
    uint256 public constant MAX_DISPUTE_AMOUNT = 1000 ether;
    uint256 public constant EVIDENCE_PERIOD = 3 days;
    uint256 public constant APPEAL_PERIOD = 2 days;
    uint256 public constant PLATFORM_FEE_BPS = 250; // 2.5%
    uint256 public constant APPEAL_STAKE_BPS = 1000; // 10%

    address public treasury;
    address public genLayerOracle;

    mapping(uint256 => Dispute) public disputes;
    mapping(uint256 => Evidence[]) public disputeEvidence;
    mapping(uint256 => AIVerdict) public aiVerdicts;
    mapping(uint256 => uint256) public appealStakes;
    mapping(address => uint256[]) public userDisputes;
    mapping(bytes32 => uint256) public genLayerRequestToDispute;

    // ============ Events ============
    event DisputeCreated(
        uint256 indexed disputeId,
        address indexed claimant,
        address indexed respondent,
        uint256 amount,
        DisputeCategory category
    );

    event EvidenceSubmitted(
        uint256 indexed disputeId,
        address indexed submitter,
        string contentHash,
        EvidenceType evidenceType
    );

    event AIVerdictRequested(
        uint256 indexed disputeId,
        bytes32 indexed genLayerRequestId
    );

    event AIVerdictReceived(
        uint256 indexed disputeId,
        Resolution resolution,
        uint8 confidenceScore
    );

    event DisputeAppealed(
        uint256 indexed disputeId,
        address indexed appealer,
        uint256 stakeAmount
    );

    event DisputeResolved(
        uint256 indexed disputeId,
        Resolution resolution,
        uint256 claimantPayout,
        uint256 respondentPayout
    );

    event DisputeCancelled(uint256 indexed disputeId);

    // ============ Modifiers ============
    modifier validDispute(uint256 _disputeId) {
        require(_disputeId > 0 && _disputeId <= disputeCounter, "Invalid dispute ID");
        _;
    }

    modifier onlyDisputeParty(uint256 _disputeId) {
        Dispute storage d = disputes[_disputeId];
        require(
            msg.sender == d.claimant || msg.sender == d.respondent,
            "Not a dispute party"
        );
        _;
    }

    // ============ Constructor ============
    constructor(address _treasury, address _genLayerOracle) {
        require(_treasury != address(0), "Invalid treasury");
        require(_genLayerOracle != address(0), "Invalid oracle");

        treasury = _treasury;
        genLayerOracle = _genLayerOracle;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(ORACLE_ROLE, _genLayerOracle);
    }

    // ============ Core Functions ============

    /**
     * @notice Create a new dispute
     * @param _respondent Address of the party being disputed against
     * @param _category Category of the dispute
     * @param _descriptionHash IPFS hash of dispute description
     */
    function createDispute(
        address _respondent,
        DisputeCategory _category,
        string calldata _descriptionHash
    ) external payable nonReentrant whenNotPaused returns (uint256) {
        require(msg.value >= MIN_DISPUTE_AMOUNT, "Amount too low");
        require(msg.value <= MAX_DISPUTE_AMOUNT, "Amount too high");
        require(_respondent != address(0), "Invalid respondent");
        require(_respondent != msg.sender, "Cannot dispute yourself");
        require(bytes(_descriptionHash).length > 0, "Description required");

        disputeCounter++;
        uint256 disputeId = disputeCounter;

        disputes[disputeId] = Dispute({
            id: disputeId,
            claimant: msg.sender,
            respondent: _respondent,
            amount: msg.value,
            createdAt: block.timestamp,
            evidenceDeadline: block.timestamp + EVIDENCE_PERIOD,
            appealDeadline: 0,
            status: DisputeStatus.Created,
            resolution: Resolution.None,
            category: _category,
            descriptionHash: _descriptionHash,
            aiConfidenceScore: 0,
            appealed: false
        });

        userDisputes[msg.sender].push(disputeId);
        userDisputes[_respondent].push(disputeId);

        emit DisputeCreated(disputeId, msg.sender, _respondent, msg.value, _category);

        return disputeId;
    }

    /**
     * @notice Respondent accepts the dispute and stakes matching amount
     * @param _disputeId ID of the dispute
     */
    function acceptDispute(uint256 _disputeId) 
        external 
        payable 
        nonReentrant 
        validDispute(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(msg.sender == d.respondent, "Not respondent");
        require(d.status == DisputeStatus.Created, "Invalid status");
        require(msg.value == d.amount, "Must match dispute amount");

        d.status = DisputeStatus.EvidenceSubmission;
        d.evidenceDeadline = block.timestamp + EVIDENCE_PERIOD;
    }

    /**
     * @notice Submit evidence for a dispute
     * @param _disputeId ID of the dispute
     * @param _contentHash IPFS hash of evidence content
     * @param _evidenceType Type of evidence being submitted
     */
    function submitEvidence(
        uint256 _disputeId,
        string calldata _contentHash,
        EvidenceType _evidenceType
    ) external validDispute(_disputeId) onlyDisputeParty(_disputeId) {
        Dispute storage d = disputes[_disputeId];
        require(
            d.status == DisputeStatus.EvidenceSubmission ||
            d.status == DisputeStatus.Created,
            "Evidence period ended"
        );
        require(block.timestamp <= d.evidenceDeadline, "Deadline passed");
        require(bytes(_contentHash).length > 0, "Content hash required");

        disputeEvidence[_disputeId].push(Evidence({
            submitter: msg.sender,
            contentHash: _contentHash,
            timestamp: block.timestamp,
            evidenceType: _evidenceType
        }));

        emit EvidenceSubmitted(_disputeId, msg.sender, _contentHash, _evidenceType);
    }

    /**
     * @notice Request AI verdict from GenLayer
     * @param _disputeId ID of the dispute
     */
    function requestAIVerdict(uint256 _disputeId) 
        external 
        validDispute(_disputeId) 
        onlyDisputeParty(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(
            d.status == DisputeStatus.EvidenceSubmission,
            "Not in evidence phase"
        );
        require(
            block.timestamp >= d.evidenceDeadline || 
            disputeEvidence[_disputeId].length >= 2,
            "Evidence period not complete"
        );

        d.status = DisputeStatus.AwaitingAIVerdict;

        // Generate unique request ID for GenLayer
        bytes32 requestId = keccak256(
            abi.encodePacked(_disputeId, block.timestamp, blockhash(block.number - 1))
        );
        genLayerRequestToDispute[requestId] = _disputeId;

        emit AIVerdictRequested(_disputeId, requestId);
    }

    /**
     * @notice Callback function for GenLayer oracle to deliver verdict
     * @param _requestId GenLayer request ID
     * @param _resolution AI determined resolution
     * @param _confidenceScore Confidence score (0-100)
     * @param _reasoningHash IPFS hash of detailed reasoning
     */
    function deliverAIVerdict(
        bytes32 _requestId,
        Resolution _resolution,
        uint8 _confidenceScore,
        string calldata _reasoningHash
    ) external onlyRole(ORACLE_ROLE) {
        uint256 disputeId = genLayerRequestToDispute[_requestId];
        require(disputeId > 0, "Unknown request");

        Dispute storage d = disputes[disputeId];
        require(d.status == DisputeStatus.AwaitingAIVerdict, "Not awaiting verdict");
        require(_resolution != Resolution.None, "Invalid resolution");
        require(_confidenceScore <= 100, "Invalid confidence score");

        aiVerdicts[disputeId] = AIVerdict({
            decision: _resolution,
            confidenceScore: _confidenceScore,
            reasoningHash: _reasoningHash,
            timestamp: block.timestamp,
            genLayerRequestId: _requestId
        });

        d.resolution = _resolution;
        d.aiConfidenceScore = _confidenceScore;
        d.status = DisputeStatus.VerdictDelivered;
        d.appealDeadline = block.timestamp + APPEAL_PERIOD;

        emit AIVerdictReceived(disputeId, _resolution, _confidenceScore);
    }

    /**
     * @notice Appeal the AI verdict
     * @param _disputeId ID of the dispute
     */
    function appealVerdict(uint256 _disputeId) 
        external 
        payable 
        nonReentrant 
        validDispute(_disputeId)
        onlyDisputeParty(_disputeId)
    {
        Dispute storage d = disputes[_disputeId];
        require(d.status == DisputeStatus.VerdictDelivered, "Cannot appeal");
        require(block.timestamp <= d.appealDeadline, "Appeal period ended");
        require(!d.appealed, "Already appealed");

        uint256 appealStake = (d.amount * 2 * APPEAL_STAKE_BPS) / 10000;
        require(msg.value >= appealStake, "Insufficient appeal stake");

        d.appealed = true;
        d.status = DisputeStatus.AppealPeriod;
        appealStakes[_disputeId] = msg.value;

        emit DisputeAppealed(_disputeId, msg.sender, msg.value);
    }

    /**
     * @notice Finalize and execute the dispute resolution
     * @param _disputeId ID of the dispute
     */
    function finalizeDispute(uint256 _disputeId) 
        external 
        nonReentrant 
        validDispute(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(
            d.status == DisputeStatus.VerdictDelivered ||
            d.status == DisputeStatus.AppealPeriod,
            "Cannot finalize"
        );
        require(
            block.timestamp > d.appealDeadline,
            "Appeal period active"
        );

        d.status = DisputeStatus.Resolved;

        uint256 totalPool = d.amount * 2;
        uint256 platformFee = (totalPool * PLATFORM_FEE_BPS) / 10000;
        uint256 distributable = totalPool - platformFee;

        uint256 claimantPayout;
        uint256 respondentPayout;

        if (d.resolution == Resolution.FavorClaimant) {
            claimantPayout = distributable;
            respondentPayout = 0;
        } else if (d.resolution == Resolution.FavorRespondent) {
            claimantPayout = 0;
            respondentPayout = distributable;
        } else if (d.resolution == Resolution.Split) {
            claimantPayout = distributable / 2;
            respondentPayout = distributable - claimantPayout;
        } else {
            // Dismissed - return original stakes minus fee
            uint256 feePerParty = platformFee / 2;
            claimantPayout = d.amount - feePerParty;
            respondentPayout = d.amount - feePerParty;
        }

        // Transfer platform fee
        (bool feeSuccess, ) = treasury.call{value: platformFee}("");
        require(feeSuccess, "Fee transfer failed");

        // Distribute to parties
        if (claimantPayout > 0) {
            (bool claimantSuccess, ) = d.claimant.call{value: claimantPayout}("");
            require(claimantSuccess, "Claimant transfer failed");
        }

        if (respondentPayout > 0) {
            (bool respondentSuccess, ) = d.respondent.call{value: respondentPayout}("");
            require(respondentSuccess, "Respondent transfer failed");
        }

        // Return appeal stake if applicable
        if (d.appealed && appealStakes[_disputeId] > 0) {
            // Logic for appeal stake distribution would go here
            // For now, return to treasury for human review
            (bool appealSuccess, ) = treasury.call{value: appealStakes[_disputeId]}("");
            require(appealSuccess, "Appeal stake transfer failed");
        }

        emit DisputeResolved(_disputeId, d.resolution, claimantPayout, respondentPayout);
    }

    /**
     * @notice Cancel dispute (only if respondent hasn't accepted)
     * @param _disputeId ID of the dispute
     */
    function cancelDispute(uint256 _disputeId) 
        external 
        nonReentrant 
        validDispute(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(msg.sender == d.claimant, "Only claimant");
        require(d.status == DisputeStatus.Created, "Cannot cancel");

        d.status = DisputeStatus.Cancelled;

        // Return claimant's stake
        (bool success, ) = d.claimant.call{value: d.amount}("");
        require(success, "Refund failed");

        emit DisputeCancelled(_disputeId);
    }

    // ============ View Functions ============

    function getDispute(uint256 _disputeId) 
        external 
        view 
        returns (Dispute memory) 
    {
        return disputes[_disputeId];
    }

    function getDisputeEvidence(uint256 _disputeId) 
        external 
        view 
        returns (Evidence[] memory) 
    {
        return disputeEvidence[_disputeId];
    }

    function getAIVerdict(uint256 _disputeId) 
        external 
        view 
        returns (AIVerdict memory) 
    {
        return aiVerdicts[_disputeId];
    }

    function getUserDisputes(address _user) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return userDisputes[_user];
    }

    function getDisputeCount() external view returns (uint256) {
        return disputeCounter;
    }

    // ============ Admin Functions ============

    function setTreasury(address _treasury) external onlyRole(ADMIN_ROLE) {
        require(_treasury != address(0), "Invalid treasury");
        treasury = _treasury;
    }

    function setGenLayerOracle(address _oracle) external onlyRole(ADMIN_ROLE) {
        require(_oracle != address(0), "Invalid oracle");
        _revokeRole(ORACLE_ROLE, genLayerOracle);
        genLayerOracle = _oracle;
        _grantRole(ORACLE_ROLE, _oracle);
    }

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    // ============ Emergency Functions ============

    function emergencyWithdraw() external onlyRole(DEFAULT_ADMIN_ROLE) {
        (bool success, ) = treasury.call{value: address(this).balance}("");
        require(success, "Withdrawal failed");
    }

    receive() external payable {}
}

contracts/EvidenceManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title EvidenceManager
 * @notice Manages evidence submission and verification for disputes
 * @dev Handles IPFS evidence references and metadata
 */
contract EvidenceManager is AccessControl, ReentrancyGuard {

    // ============ Roles ============
    bytes32 public constant DISPUTE_CONTRACT_ROLE = keccak256("DISPUTE_CONTRACT_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    // ============ Enums ============
    enum EvidenceType {
        Document,
        Image,
        Video,
        Audio,
        Contract,
        Communication,
        Transaction,
        Testimony,
        ExpertReport,
        Other
    }

    enum VerificationStatus {
        Unverified,
        Verified,
        Rejected,
        Disputed
    }

    // ============ Structs ============
    struct EvidenceItem {
        uint256 id;
        uint256 disputeId;
        address submitter;
        string ipfsHash;
        string metadataHash;
        EvidenceType evidenceType;
        uint256 timestamp;
        VerificationStatus status;
        uint256 fileSize;
        string mimeType;
        bool isConfidential;
    }

    struct EvidenceMetadata {
        string title;
        string description;
        string[] tags;
        uint256 relevanceScore; // 0-100, set by AI
    }

    // ============ State Variables ============
    uint256 public evidenceCounter;
    uint256 public constant MAX_EVIDENCE_PER_DISPUTE = 50;
    uint256 public constant MAX_FILE_SIZE = 100 * 1024 * 1024; // 100MB

    mapping(uint256 => EvidenceItem) public evidenceItems;
    mapping(uint256 => uint256[]) public disputeEvidence;
    mapping(uint256 => EvidenceMetadata) public evidenceMetadata;
    mapping(address => uint256[]) public submitterEvidence;
    mapping(string => bool) public usedHashes;

    // Allowed MIME types
    mapping(string => bool) public allowedMimeTypes;

    // ============ Events ============
    event EvidenceSubmitted(
        uint256 indexed evidenceId,
        uint256 indexed disputeId,
        address indexed submitter,
        string ipfsHash,
        EvidenceType evidenceType
    );

    event EvidenceVerified(
        uint256 indexed evidenceId,
        VerificationStatus status,
        address verifier
    );

    event EvidenceMetadataUpdated(
        uint256 indexed evidenceId,
        string title,
        uint256 relevanceScore
    );

    event EvidenceRemoved(
        uint256 indexed evidenceId,
        uint256 indexed disputeId,
        string reason
    );

    // ============ Constructor ============
    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

        // Initialize allowed MIME types
        allowedMimeTypes["application/pdf"] = true;
        allowedMimeTypes["image/jpeg"] = true;
        allowedMimeTypes["image/png"] = true;
        allowedMimeTypes["image/gif"] = true;
        allowedMimeTypes["video/mp4"] = true;
        allowedMimeTypes["audio/mpeg"] = true;
        allowedMimeTypes["text/plain"] = true;
        allowedMimeTypes["application/json"] = true;
        allowedMimeTypes["application/msword"] = true;
        allowedMimeTypes["application/vnd.openxmlformats-officedocument.wordprocessingml.document"] = true;
    }

    // ============ Core Functions ============

    /**
     * @notice Submit evidence for a dispute
     * @param _disputeId ID of the dispute
     * @param _ipfsHash IPFS hash of evidence content
     * @param _metadataHash IPFS hash of evidence metadata
     * @param _evidenceType Type of evidence
     * @param _fileSize Size of the file in bytes
     * @param _mimeType MIME type of the file
     * @param _isConfidential Whether evidence should be treated as confidential
     */
    function submitEvidence(
        uint256 _disputeId,
        string calldata _ipfsHash,
        string calldata _metadataHash,
        EvidenceType _evidenceType,
        uint256 _fileSize,
        string calldata _mimeType,
        bool _isConfidential
    ) external nonReentrant returns (uint256) {
        require(_disputeId > 0, "Invalid dispute ID");
        require(bytes(_ipfsHash).length == 46, "Invalid IPFS hash"); // CIDv0 length
        require(!usedHashes[_ipfsHash], "Evidence already submitted");
        require(_fileSize <= MAX_FILE_SIZE, "File too large");
        require(
            disputeEvidence[_disputeId].length < MAX_EVIDENCE_PER_DISPUTE,
            "Max evidence reached"
        );
        require(
            allowedMimeTypes[_mimeType] || bytes(_mimeType).length == 0,
            "Invalid MIME type"
        );

        evidenceCounter++;
        uint256 evidenceId = evidenceCounter;

        evidenceItems[evidenceId] = EvidenceItem({
            id: evidenceId,
            disputeId: _disputeId,
            submitter: msg.sender,
            ipfsHash: _ipfsHash,
            metadataHash: _metadataHash,
            evidenceType: _evidenceType,
            timestamp: block.timestamp,
            status: VerificationStatus.Unverified,
            fileSize: _fileSize,
            mimeType: _mimeType,
            isConfidential: _isConfidential
        });

        disputeEvidence[_disputeId].push(evidenceId);
        submitterEvidence[msg.sender].push(evidenceId);
        usedHashes[_ipfsHash] = true;

        emit EvidenceSubmitted(
            evidenceId,
            _disputeId,
            msg.sender,
            _ipfsHash,
            _evidenceType
        );

        return evidenceId;
    }

    /**
     * @notice Batch submit multiple evidence items
     * @param _disputeId ID of the dispute
     * @param _ipfsHashes Array of IPFS hashes
     * @param _metadataHashes Array of metadata hashes
     * @param _evidenceTypes Array of evidence types
     */
    function batchSubmitEvidence(
        uint256 _disputeId,
        string[] calldata _ipfsHashes,
        string[] calldata _metadataHashes,
        EvidenceType[] calldata _evidenceTypes
    ) external nonReentrant returns (uint256[] memory) {
        require(
            _ipfsHashes.length == _metadataHashes.length &&
            _ipfsHashes.length == _evidenceTypes.length,
            "Array length mismatch"
        );
        require(_ipfsHashes.length <= 10, "Max 10 items per batch");
        require(
            disputeEvidence[_disputeId].length + _ipfsHashes.length <= MAX_EVIDENCE_PER_DISPUTE,
            "Would exceed max evidence"
        );

        uint256[] memory evidenceIds = new uint256[](_ipfsHashes.length);

        for (uint256 i = 0; i < _ipfsHashes.length; i++) {
            require(!usedHashes[_ipfsHashes[i]], "Duplicate hash");

            evidenceCounter++;
            uint256 evidenceId = evidenceCounter;

            evidenceItems[evidenceId] = EvidenceItem({
                id: evidenceId,
                disputeId: _disputeId,
                submitter: msg.sender,
                ipfsHash: _ipfsHashes[i],
                metadataHash: _metadataHashes[i],
                evidenceType: _evidenceTypes[i],
                timestamp: block.timestamp,
                status: VerificationStatus.Unverified,
                fileSize: 0,
                mimeType: "",
                isConfidential: false
            });

            disputeEvidence[_disputeId].push(evidenceId);
            submitterEvidence[msg.sender].push(evidenceId);
            usedHashes[_ipfsHashes[i]] = true;
            evidenceIds[i] = evidenceId;

            emit EvidenceSubmitted(
                evidenceId,
                _disputeId,
                msg.sender,
                _ipfsHashes[i],
                _evidenceTypes[i]
            );
        }

        return evidenceIds;
    }

    /**
     * @notice Verify evidence (by authorized verifier)
     * @param _evidenceId ID of the evidence
     * @param _status New verification status
     */
    function verifyEvidence(
        uint256 _evidenceId,
        VerificationStatus _status
    ) external onlyRole(VERIFIER_ROLE) {
        require(_evidenceId > 0 && _evidenceId <= evidenceCounter, "Invalid ID");
        require(_status != VerificationStatus.Unverified, "Invalid status");

        EvidenceItem storage item = evidenceItems[_evidenceId];
        require(item.status == VerificationStatus.Unverified, "Already verified");

        item.status = _status;

        emit EvidenceVerified(_evidenceId, _status, msg.sender);
    }

    /**
     * @notice Batch verify multiple evidence items
     * @param _evidenceIds Array of evidence IDs
     * @param _statuses Array of verification statuses
     */
    function batchVerifyEvidence(
        uint256[] calldata _evidenceIds,
        VerificationStatus[] calldata _statuses
    ) external onlyRole(VERIFIER_ROLE) {
        require(_evidenceIds.length == _statuses.length, "Array mismatch");
        require(_evidenceIds.length <= 50, "Max 50 per batch");

        for (uint256 i = 0; i < _evidenceIds.length; i++) {
            EvidenceItem storage item = evidenceItems[_evidenceIds[i]];
            if (item.status == VerificationStatus.Unverified) {
                item.status = _statuses[i];
                emit EvidenceVerified(_evidenceIds[i], _statuses[i], msg.sender);
            }
        }
    }

    /**
     * @notice Update evidence metadata (by AI or verifier)
     * @param _evidenceId ID of the evidence
     * @param _title Evidence title
     * @param _description Evidence description
     * @param _tags Evidence tags
     * @param _relevanceScore AI-determined relevance score
     */
    function updateMetadata(
        uint256 _evidenceId,
        string calldata _title,
        string calldata _description,
        string[] calldata _tags,
        uint256 _relevanceScore
    ) external onlyRole(VERIFIER_ROLE) {
        require(_evidenceId > 0 && _evidenceId <= evidenceCounter, "Invalid ID");
        require(_relevanceScore <= 100, "Invalid score");

        evidenceMetadata[_evidenceId] = EvidenceMetadata({
            title: _title,
            description: _description,
            tags: _tags,
            relevanceScore: _relevanceScore
        });

        emit EvidenceMetadataUpdated(_evidenceId, _title, _relevanceScore);
    }

    // ============ View Functions ============

    function getEvidence(uint256 _evidenceId) 
        external 
        view 
        returns (EvidenceItem memory) 
    {
        return evidenceItems[_evidenceId];
    }

    function getDisputeEvidence(uint256 _disputeId) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return disputeEvidence[_disputeId];
    }

    function getDisputeEvidenceItems(uint256 _disputeId) 
        external 
        view 
        returns (EvidenceItem[] memory) 
    {
        uint256[] memory ids = disputeEvidence[_disputeId];
        EvidenceItem[] memory items = new EvidenceItem[](ids.length);

        for (uint256 i = 0; i < ids.length; i++) {
            items[i] = evidenceItems[ids[i]];
        }

        return items;
    }

    function getSubmitterEvidence(address _submitter) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return submitterEvidence[_submitter];
    }

    function getEvidenceMetadata(uint256 _evidenceId) 
        external 
        view 
        returns (EvidenceMetadata memory) 
    {
        return evidenceMetadata[_evidenceId];
    }

    function getEvidenceCount() external view returns (uint256) {
        return evidenceCounter;
    }

    function getDisputeEvidenceCount(uint256 _disputeId) 
        external 
        view 
        returns (uint256) 
    {
        return disputeEvidence[_disputeId].length;
    }

    function isHashUsed(string calldata _hash) external view returns (bool) {
        return usedHashes[_hash];
    }

    // ============ Admin Functions ============

    function setDisputeContract(address _contract) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _grantRole(DISPUTE_CONTRACT_ROLE, _contract);
    }

    function addVerifier(address _verifier) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _grantRole(VERIFIER_ROLE, _verifier);
    }

    function removeVerifier(address _verifier) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _revokeRole(VERIFIER_ROLE, _verifier);
    }

    function setAllowedMimeType(string calldata _mimeType, bool _allowed) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        allowedMimeTypes[_mimeType] = _allowed;
    }
}

contracts/GenLayerOracleAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title GenLayerOracleAdapter
 * @notice Adapter contract for GenLayer AI consensus integration
 * @dev Handles communication between Base L2 and GenLayer network
 */
contract GenLayerOracleAdapter is Ownable, ReentrancyGuard {

    // ============ Structs ============
    struct OracleRequest {
        uint256 disputeId;
        address disputeContract;
        bytes32 requestId;
        uint256 timestamp;
        RequestStatus status;
        string prompt;
    }

    enum RequestStatus {
        Pending,
        Processing,
        Fulfilled,
        Failed,
        Expired
    }

    // ============ State Variables ============
    address public disputeResolutionContract;
    address public genLayerRelayer;

    uint256 public requestCounter;
    uint256 public constant REQUEST_TIMEOUT = 24 hours;
    uint256 public oracleFee = 0.001 ether;

    mapping(bytes32 => OracleRequest) public requests;
    mapping(uint256 => bytes32) public disputeToRequest;

    // AI prompt template for dispute analysis
    string public aiPromptTemplate;

    // ============ Events ============
    event OracleRequestCreated(
        bytes32 indexed requestId,
        uint256 indexed disputeId,
        string prompt
    );

    event OracleRequestFulfilled(
        bytes32 indexed requestId,
        uint256 indexed disputeId,
        uint8 resolution,
        uint8 confidenceScore
    );

    event OracleRequestFailed(
        bytes32 indexed requestId,
        uint256 indexed disputeId,
        string reason
    );

    event RelayerUpdated(address indexed oldRelayer, address indexed newRelayer);
    event FeeUpdated(uint256 oldFee, uint256 newFee);

    // ============ Modifiers ============
    modifier onlyDisputeContract() {
        require(
            msg.sender == disputeResolutionContract,
            "Only dispute contract"
        );
        _;
    }

    modifier onlyRelayer() {
        require(msg.sender == genLayerRelayer, "Only relayer");
        _;
    }

    // ============ Constructor ============
    constructor(address _genLayerRelayer) Ownable(msg.sender) {
        require(_genLayerRelayer != address(0), "Invalid relayer");
        genLayerRelayer = _genLayerRelayer;

        // Set default AI prompt template
        aiPromptTemplate = "Analyze the following dispute and provide a fair resolution. "
            "Consider all submitted evidence, the dispute category, and applicable standards. "
            "Return a resolution (1=FavorClaimant, 2=FavorRespondent, 3=Split, 4=Dismissed) "
            "and a confidence score (0-100).";
    }

    // ============ Core Functions ============

    /**
     * @notice Request AI analysis for a dispute
     * @param _disputeId ID of the dispute
     * @param _disputeData Encoded dispute data for AI analysis
     */
    function requestAIAnalysis(
        uint256 _disputeId,
        bytes calldata _disputeData
    ) external payable onlyDisputeContract nonReentrant returns (bytes32) {
        require(msg.value >= oracleFee, "Insufficient fee");
        require(disputeToRequest[_disputeId] == bytes32(0), "Request exists");

        requestCounter++;

        bytes32 requestId = keccak256(
            abi.encodePacked(
                _disputeId,
                requestCounter,
                block.timestamp,
                blockhash(block.number - 1)
            )
        );

        // Build the prompt with dispute data
        string memory prompt = _buildPrompt(_disputeData);

        requests[requestId] = OracleRequest({
            disputeId: _disputeId,
            disputeContract: disputeResolutionContract,
            requestId: requestId,
            timestamp: block.timestamp,
            status: RequestStatus.Pending,
            prompt: prompt
        });

        disputeToRequest[_disputeId] = requestId;

        emit OracleRequestCreated(requestId, _disputeId, prompt);

        return requestId;
    }

    /**
     * @notice Fulfill oracle request with AI verdict
     * @param _requestId Request ID
     * @param _resolution AI resolution (1-4)
     * @param _confidenceScore Confidence score (0-100)
     * @param _reasoningHash IPFS hash of detailed reasoning
     */
    function fulfillRequest(
        bytes32 _requestId,
        uint8 _resolution,
        uint8 _confidenceScore,
        string calldata _reasoningHash
    ) external onlyRelayer nonReentrant {
        OracleRequest storage request = requests[_requestId];
        require(request.disputeId > 0, "Request not found");
        require(request.status == RequestStatus.Pending, "Invalid status");
        require(_resolution >= 1 && _resolution <= 4, "Invalid resolution");
        require(_confidenceScore <= 100, "Invalid confidence");

        request.status = RequestStatus.Fulfilled;

        // Call back to dispute resolution contract
        IDisputeResolution(request.disputeContract).deliverAIVerdict(
            _requestId,
            IDisputeResolution.Resolution(_resolution),
            _confidenceScore,
            _reasoningHash
        );

        emit OracleRequestFulfilled(
            _requestId,
            request.disputeId,
            _resolution,
            _confidenceScore
        );
    }

    /**
     * @notice Mark request as failed
     * @param _requestId Request ID
     * @param _reason Failure reason
     */
    function failRequest(
        bytes32 _requestId,
        string calldata _reason
    ) external onlyRelayer {
        OracleRequest storage request = requests[_requestId];
        require(request.disputeId > 0, "Request not found");
        require(
            request.status == RequestStatus.Pending ||
            request.status == RequestStatus.Processing,
            "Invalid status"
        );

        request.status = RequestStatus.Failed;

        emit OracleRequestFailed(_requestId, request.disputeId, _reason);
    }

    /**
     * @notice Handle expired requests
     * @param _requestId Request ID
     */
    function handleExpiredRequest(bytes32 _requestId) external {
        OracleRequest storage request = requests[_requestId];
        require(request.disputeId > 0, "Request not found");
        require(request.status == RequestStatus.Pending, "Not pending");
        require(
            block.timestamp > request.timestamp + REQUEST_TIMEOUT,
            "Not expired"
        );

        request.status = RequestStatus.Expired;

        emit OracleRequestFailed(_requestId, request.disputeId, "Request expired");
    }

    // ============ Internal Functions ============

    /**
     * @dev Build AI prompt from dispute data
     */
    function _buildPrompt(bytes calldata _disputeData) 
        internal 
        view 
        returns (string memory) 
    {
        // Decode dispute data
        (
            uint256 disputeId,
            uint8 category,
            string memory descriptionHash,
            string[] memory evidenceHashes
        ) = abi.decode(_disputeData, (uint256, uint8, string, string[]));

        // Build comprehensive prompt
        string memory prompt = string(abi.encodePacked(
            aiPromptTemplate,
            "\n\nDispute ID: ", _uint2str(disputeId),
            "\nCategory: ", _getCategoryName(category),
            "\nDescription IPFS: ", descriptionHash,
            "\nEvidence count: ", _uint2str(evidenceHashes.length)
        ));

        return prompt;
    }

    /**
     * @dev Convert uint to string
     */
    function _uint2str(uint256 _i) internal pure returns (string memory) {
        if (_i == 0) return "0";
        uint256 j = _i;
        uint256 length;
        while (j != 0) {
            length++;
            j /= 10;
        }
        bytes memory bstr = new bytes(length);
        uint256 k = length;
        j = _i;
        while (j != 0) {
            bstr[--k] = bytes1(uint8(48 + j % 10));
            j /= 10;
        }
        return string(bstr);
    }

    /**
     * @dev Get category name from enum value
     */
    function _getCategoryName(uint8 _category) 
        internal 
        pure 
        returns (string memory) 
    {
        if (_category == 0) return "ContractBreach";
        if (_category == 1) return "ServiceQuality";
        if (_category == 2) return "PaymentDispute";
        if (_category == 3) return "IntellectualProperty";
        if (_category == 4) return "FraudClaim";
        return "Other";
    }

    // ============ View Functions ============

    function getRequest(bytes32 _requestId) 
        external 
        view 
        returns (OracleRequest memory) 
    {
        return requests[_requestId];
    }

    function getRequestByDispute(uint256 _disputeId) 
        external 
        view 
        returns (OracleRequest memory) 
    {
        bytes32 requestId = disputeToRequest[_disputeId];
        return requests[requestId];
    }

    function isRequestPending(bytes32 _requestId) 
        external 
        view 
        returns (bool) 
    {
        return requests[_requestId].status == RequestStatus.Pending;
    }

    // ============ Admin Functions ============

    function setDisputeResolutionContract(address _contract) 
        external 
        onlyOwner 
    {
        require(_contract != address(0), "Invalid address");
        disputeResolutionContract = _contract;
    }

    function setRelayer(address _relayer) external onlyOwner {
        require(_relayer != address(0), "Invalid relayer");
        address oldRelayer = genLayerRelayer;
        genLayerRelayer = _relayer;
        emit RelayerUpdated(oldRelayer, _relayer);
    }

    function setOracleFee(uint256 _fee) external onlyOwner {
        uint256 oldFee = oracleFee;
        oracleFee = _fee;
        emit FeeUpdated(oldFee, _fee);
    }

    function setPromptTemplate(string calldata _template) external onlyOwner {
        aiPromptTemplate = _template;
    }

    function withdrawFees() external onlyOwner {
        (bool success, ) = owner().call{value: address(this).balance}("");
        require(success, "Withdrawal failed");
    }

    receive() external payable {}
}

// ============ Interface ============
interface IDisputeResolution {
    enum Resolution {
        None,
        FavorClaimant,
        FavorRespondent,
        Split,
        Dismissed
    }

    function deliverAIVerdict(
        bytes32 _requestId,
        Resolution _resolution,
        uint8 _confidenceScore,
        string calldata _reasoningHash
    ) external;
}

contracts/MultiTokenDisputeResolution.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title MultiTokenDisputeResolution
 * @notice Dispute resolution supporting multiple ERC20 tokens and ETH
 * @dev Extends base functionality with multi-token support
 */
contract MultiTokenDisputeResolution is ReentrancyGuard, AccessControl, Pausable {
    using SafeERC20 for IERC20;

    // ============ Roles ============
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant TOKEN_MANAGER_ROLE = keccak256("TOKEN_MANAGER_ROLE");

    // ============ Enums ============
    enum DisputeStatus {
        Created,
        EvidenceSubmission,
        AwaitingAIVerdict,
        VerdictDelivered,
        AppealPeriod,
        Resolved,
        Cancelled
    }

    enum Resolution {
        None,
        FavorClaimant,
        FavorRespondent,
        Split,
        Dismissed
    }

    enum DisputeCategory {
        ContractBreach,
        ServiceQuality,
        PaymentDispute,
        IntellectualProperty,
        FraudClaim,
        Other
    }

    // ============ Structs ============
    struct TokenConfig {
        bool isSupported;
        uint256 minAmount;
        uint256 maxAmount;
        uint256 platformFeeBps;    // Can have different fees per token
        address priceFeed;         // Chainlink price feed for USD conversion
        uint8 decimals;
    }

    struct Dispute {
        uint256 id;
        address claimant;
        address respondent;
        address token;             // address(0) for ETH
        uint256 amount;
        uint256 createdAt;
        uint256 evidenceDeadline;
        uint256 appealDeadline;
        DisputeStatus status;
        Resolution resolution;
        DisputeCategory category;
        string descriptionHash;
        uint8 aiConfidenceScore;
        bool appealed;
    }

    struct Evidence {
        address submitter;
        string contentHash;
        uint256 timestamp;
        uint8 evidenceType;
    }

    struct AIVerdict {
        Resolution decision;
        uint8 confidenceScore;
        string reasoningHash;
        uint256 timestamp;
        bytes32 genLayerRequestId;
    }

    // ============ State Variables ============
    uint256 public disputeCounter;
    uint256 public constant EVIDENCE_PERIOD = 3 days;
    uint256 public constant APPEAL_PERIOD = 2 days;
    uint256 public constant DEFAULT_FEE_BPS = 250;      // 2.5%
    uint256 public constant APPEAL_STAKE_BPS = 1000;    // 10%

    // ETH config
    uint256 public minEthAmount = 0.001 ether;
    uint256 public maxEthAmount = 1000 ether;

    address public treasury;
    address public genLayerOracle;

    // Token configurations
    mapping(address => TokenConfig) public tokenConfigs;
    address[] public supportedTokens;

    // Dispute data
    mapping(uint256 => Dispute) public disputes;
    mapping(uint256 => Evidence[]) public disputeEvidence;
    mapping(uint256 => AIVerdict) public aiVerdicts;
    mapping(uint256 => uint256) public appealStakes;
    mapping(address => uint256[]) public userDisputes;
    mapping(bytes32 => uint256) public genLayerRequestToDispute;

    // Token balances tracking
    mapping(uint256 => mapping(address => uint256)) public disputeTokenBalances;

    // ============ Events ============
    event TokenAdded(address indexed token, uint256 minAmount, uint256 maxAmount, uint256 feeBps);
    event TokenRemoved(address indexed token);
    event TokenConfigUpdated(address indexed token, uint256 minAmount, uint256 maxAmount, uint256 feeBps);

    event DisputeCreated(
        uint256 indexed disputeId,
        address indexed claimant,
        address indexed respondent,
        address token,
        uint256 amount,
        DisputeCategory category
    );

    event EvidenceSubmitted(
        uint256 indexed disputeId,
        address indexed submitter,
        string contentHash,
        uint8 evidenceType
    );

    event AIVerdictRequested(uint256 indexed disputeId, bytes32 indexed genLayerRequestId);
    event AIVerdictReceived(uint256 indexed disputeId, Resolution resolution, uint8 confidenceScore);
    event DisputeAppealed(uint256 indexed disputeId, address indexed appealer, uint256 stakeAmount);
    event DisputeResolved(uint256 indexed disputeId, Resolution resolution, uint256 claimantPayout, uint256 respondentPayout);
    event DisputeCancelled(uint256 indexed disputeId);

    // ============ Modifiers ============
    modifier validDispute(uint256 _disputeId) {
        require(_disputeId > 0 && _disputeId <= disputeCounter, "Invalid dispute ID");
        _;
    }

    modifier onlyDisputeParty(uint256 _disputeId) {
        Dispute storage d = disputes[_disputeId];
        require(msg.sender == d.claimant || msg.sender == d.respondent, "Not a dispute party");
        _;
    }

    modifier supportedToken(address _token) {
        require(_token == address(0) || tokenConfigs[_token].isSupported, "Token not supported");
        _;
    }

    // ============ Constructor ============
    constructor(address _treasury, address _genLayerOracle) {
        require(_treasury != address(0), "Invalid treasury");
        require(_genLayerOracle != address(0), "Invalid oracle");

        treasury = _treasury;
        genLayerOracle = _genLayerOracle;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(TOKEN_MANAGER_ROLE, msg.sender);
        _grantRole(ORACLE_ROLE, _genLayerOracle);
    }

    // ============ Token Management ============

    /**
     * @notice Add a supported ERC20 token
     * @param _token Token address
     * @param _minAmount Minimum dispute amount
     * @param _maxAmount Maximum dispute amount
     * @param _feeBps Platform fee in basis points
     * @param _priceFeed Chainlink price feed address (optional)
     */
    function addSupportedToken(
        address _token,
        uint256 _minAmount,
        uint256 _maxAmount,
        uint256 _feeBps,
        address _priceFeed
    ) external onlyRole(TOKEN_MANAGER_ROLE) {
        require(_token != address(0), "Invalid token");
        require(!tokenConfigs[_token].isSupported, "Already supported");
        require(_minAmount < _maxAmount, "Invalid amounts");
        require(_feeBps <= 1000, "Fee too high"); // Max 10%

        uint8 decimals = 18;
        try IERC20Metadata(_token).decimals() returns (uint8 d) {
            decimals = d;
        } catch {}

        tokenConfigs[_token] = TokenConfig({
            isSupported: true,
            minAmount: _minAmount,
            maxAmount: _maxAmount,
            platformFeeBps: _feeBps,
            priceFeed: _priceFeed,
            decimals: decimals
        });

        supportedTokens.push(_token);
        emit TokenAdded(_token, _minAmount, _maxAmount, _feeBps);
    }

    /**
     * @notice Remove a supported token
     */
    function removeSupportedToken(address _token) external onlyRole(TOKEN_MANAGER_ROLE) {
        require(tokenConfigs[_token].isSupported, "Not supported");
        tokenConfigs[_token].isSupported = false;
        emit TokenRemoved(_token);
    }

    /**
     * @notice Update token configuration
     */
    function updateTokenConfig(
        address _token,
        uint256 _minAmount,
        uint256 _maxAmount,
        uint256 _feeBps,
        address _priceFeed
    ) external onlyRole(TOKEN_MANAGER_ROLE) {
        require(tokenConfigs[_token].isSupported, "Not supported");
        require(_minAmount < _maxAmount, "Invalid amounts");
        require(_feeBps <= 1000, "Fee too high");

        tokenConfigs[_token].minAmount = _minAmount;
        tokenConfigs[_token].maxAmount = _maxAmount;
        tokenConfigs[_token].platformFeeBps = _feeBps;
        tokenConfigs[_token].priceFeed = _priceFeed;

        emit TokenConfigUpdated(_token, _minAmount, _maxAmount, _feeBps);
    }

    // ============ Core Functions ============

    /**
     * @notice Create a dispute with ETH
     */
    function createDisputeETH(
        address _respondent,
        DisputeCategory _category,
        string calldata _descriptionHash
    ) external payable nonReentrant whenNotPaused returns (uint256) {
        require(msg.value >= minEthAmount, "Amount too low");
        require(msg.value <= maxEthAmount, "Amount too high");

        return _createDispute(_respondent, address(0), msg.value, _category, _descriptionHash);
    }

    /**
     * @notice Create a dispute with ERC20 token
     */
    function createDisputeToken(
        address _respondent,
        address _token,
        uint256 _amount,
        DisputeCategory _category,
        string calldata _descriptionHash
    ) external nonReentrant whenNotPaused supportedToken(_token) returns (uint256) {
        TokenConfig memory config = tokenConfigs[_token];
        require(_amount >= config.minAmount, "Amount too low");
        require(_amount <= config.maxAmount, "Amount too high");

        // Transfer tokens from claimant
        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);

        return _createDispute(_respondent, _token, _amount, _category, _descriptionHash);
    }

    function _createDispute(
        address _respondent,
        address _token,
        uint256 _amount,
        DisputeCategory _category,
        string calldata _descriptionHash
    ) internal returns (uint256) {
        require(_respondent != address(0), "Invalid respondent");
        require(_respondent != msg.sender, "Cannot dispute yourself");
        require(bytes(_descriptionHash).length > 0, "Description required");

        disputeCounter++;
        uint256 disputeId = disputeCounter;

        disputes[disputeId] = Dispute({
            id: disputeId,
            claimant: msg.sender,
            respondent: _respondent,
            token: _token,
            amount: _amount,
            createdAt: block.timestamp,
            evidenceDeadline: block.timestamp + EVIDENCE_PERIOD,
            appealDeadline: 0,
            status: DisputeStatus.Created,
            resolution: Resolution.None,
            category: _category,
            descriptionHash: _descriptionHash,
            aiConfidenceScore: 0,
            appealed: false
        });

        disputeTokenBalances[disputeId][_token] = _amount;
        userDisputes[msg.sender].push(disputeId);
        userDisputes[_respondent].push(disputeId);

        emit DisputeCreated(disputeId, msg.sender, _respondent, _token, _amount, _category);
        return disputeId;
    }

    /**
     * @notice Accept dispute with ETH
     */
    function acceptDisputeETH(uint256 _disputeId) 
        external 
        payable 
        nonReentrant 
        validDispute(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(msg.sender == d.respondent, "Not respondent");
        require(d.status == DisputeStatus.Created, "Invalid status");
        require(d.token == address(0), "Not ETH dispute");
        require(msg.value == d.amount, "Must match amount");

        d.status = DisputeStatus.EvidenceSubmission;
        d.evidenceDeadline = block.timestamp + EVIDENCE_PERIOD;
        disputeTokenBalances[_disputeId][address(0)] += msg.value;
    }

    /**
     * @notice Accept dispute with ERC20 token
     */
    function acceptDisputeToken(uint256 _disputeId) 
        external 
        nonReentrant 
        validDispute(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(msg.sender == d.respondent, "Not respondent");
        require(d.status == DisputeStatus.Created, "Invalid status");
        require(d.token != address(0), "Not token dispute");

        IERC20(d.token).safeTransferFrom(msg.sender, address(this), d.amount);

        d.status = DisputeStatus.EvidenceSubmission;
        d.evidenceDeadline = block.timestamp + EVIDENCE_PERIOD;
        disputeTokenBalances[_disputeId][d.token] += d.amount;
    }

    /**
     * @notice Submit evidence
     */
    function submitEvidence(
        uint256 _disputeId,
        string calldata _contentHash,
        uint8 _evidenceType
    ) external validDispute(_disputeId) onlyDisputeParty(_disputeId) {
        Dispute storage d = disputes[_disputeId];
        require(
            d.status == DisputeStatus.EvidenceSubmission || d.status == DisputeStatus.Created,
            "Evidence period ended"
        );
        require(block.timestamp <= d.evidenceDeadline, "Deadline passed");

        disputeEvidence[_disputeId].push(Evidence({
            submitter: msg.sender,
            contentHash: _contentHash,
            timestamp: block.timestamp,
            evidenceType: _evidenceType
        }));

        emit EvidenceSubmitted(_disputeId, msg.sender, _contentHash, _evidenceType);
    }

    /**
     * @notice Request AI verdict
     */
    function requestAIVerdict(uint256 _disputeId) 
        external 
        validDispute(_disputeId) 
        onlyDisputeParty(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(d.status == DisputeStatus.EvidenceSubmission, "Not in evidence phase");
        require(
            block.timestamp >= d.evidenceDeadline || disputeEvidence[_disputeId].length >= 2,
            "Evidence period not complete"
        );

        d.status = DisputeStatus.AwaitingAIVerdict;

        bytes32 requestId = keccak256(
            abi.encodePacked(_disputeId, block.timestamp, blockhash(block.number - 1))
        );
        genLayerRequestToDispute[requestId] = _disputeId;

        emit AIVerdictRequested(_disputeId, requestId);
    }

    /**
     * @notice Deliver AI verdict (oracle only)
     */
    function deliverAIVerdict(
        bytes32 _requestId,
        Resolution _resolution,
        uint8 _confidenceScore,
        string calldata _reasoningHash
    ) external onlyRole(ORACLE_ROLE) {
        uint256 disputeId = genLayerRequestToDispute[_requestId];
        require(disputeId > 0, "Unknown request");

        Dispute storage d = disputes[disputeId];
        require(d.status == DisputeStatus.AwaitingAIVerdict, "Not awaiting verdict");

        aiVerdicts[disputeId] = AIVerdict({
            decision: _resolution,
            confidenceScore: _confidenceScore,
            reasoningHash: _reasoningHash,
            timestamp: block.timestamp,
            genLayerRequestId: _requestId
        });

        d.resolution = _resolution;
        d.aiConfidenceScore = _confidenceScore;
        d.status = DisputeStatus.VerdictDelivered;
        d.appealDeadline = block.timestamp + APPEAL_PERIOD;

        emit AIVerdictReceived(disputeId, _resolution, _confidenceScore);
    }

    /**
     * @notice Appeal verdict
     */
    function appealVerdictETH(uint256 _disputeId) 
        external 
        payable 
        nonReentrant 
        validDispute(_disputeId)
        onlyDisputeParty(_disputeId)
    {
        Dispute storage d = disputes[_disputeId];
        require(d.status == DisputeStatus.VerdictDelivered, "Cannot appeal");
        require(block.timestamp <= d.appealDeadline, "Appeal period ended");
        require(!d.appealed, "Already appealed");
        require(d.token == address(0), "Use appealVerdictToken for token disputes");

        uint256 appealStake = (d.amount * 2 * APPEAL_STAKE_BPS) / 10000;
        require(msg.value >= appealStake, "Insufficient stake");

        d.appealed = true;
        d.status = DisputeStatus.AppealPeriod;
        appealStakes[_disputeId] = msg.value;

        emit DisputeAppealed(_disputeId, msg.sender, msg.value);
    }

    /**
     * @notice Appeal verdict with token
     */
    function appealVerdictToken(uint256 _disputeId) 
        external 
        nonReentrant 
        validDispute(_disputeId)
        onlyDisputeParty(_disputeId)
    {
        Dispute storage d = disputes[_disputeId];
        require(d.status == DisputeStatus.VerdictDelivered, "Cannot appeal");
        require(block.timestamp <= d.appealDeadline, "Appeal period ended");
        require(!d.appealed, "Already appealed");
        require(d.token != address(0), "Use appealVerdictETH for ETH disputes");

        uint256 appealStake = (d.amount * 2 * APPEAL_STAKE_BPS) / 10000;
        IERC20(d.token).safeTransferFrom(msg.sender, address(this), appealStake);

        d.appealed = true;
        d.status = DisputeStatus.AppealPeriod;
        appealStakes[_disputeId] = appealStake;

        emit DisputeAppealed(_disputeId, msg.sender, appealStake);
    }

    /**
     * @notice Finalize dispute and distribute funds
     */
    function finalizeDispute(uint256 _disputeId) 
        external 
        nonReentrant 
        validDispute(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(
            d.status == DisputeStatus.VerdictDelivered || d.status == DisputeStatus.AppealPeriod,
            "Cannot finalize"
        );
        require(block.timestamp > d.appealDeadline, "Appeal period active");

        d.status = DisputeStatus.Resolved;

        uint256 totalPool = d.amount * 2;
        uint256 feeBps = d.token == address(0) ? DEFAULT_FEE_BPS : tokenConfigs[d.token].platformFeeBps;
        uint256 platformFee = (totalPool * feeBps) / 10000;
        uint256 distributable = totalPool - platformFee;

        uint256 claimantPayout;
        uint256 respondentPayout;

        if (d.resolution == Resolution.FavorClaimant) {
            claimantPayout = distributable;
        } else if (d.resolution == Resolution.FavorRespondent) {
            respondentPayout = distributable;
        } else if (d.resolution == Resolution.Split) {
            claimantPayout = distributable / 2;
            respondentPayout = distributable - claimantPayout;
        } else {
            uint256 feePerParty = platformFee / 2;
            claimantPayout = d.amount - feePerParty;
            respondentPayout = d.amount - feePerParty;
        }

        // Transfer funds
        if (d.token == address(0)) {
            _transferETH(treasury, platformFee);
            if (claimantPayout > 0) _transferETH(d.claimant, claimantPayout);
            if (respondentPayout > 0) _transferETH(d.respondent, respondentPayout);
            if (d.appealed && appealStakes[_disputeId] > 0) {
                _transferETH(treasury, appealStakes[_disputeId]);
            }
        } else {
            IERC20(d.token).safeTransfer(treasury, platformFee);
            if (claimantPayout > 0) IERC20(d.token).safeTransfer(d.claimant, claimantPayout);
            if (respondentPayout > 0) IERC20(d.token).safeTransfer(d.respondent, respondentPayout);
            if (d.appealed && appealStakes[_disputeId] > 0) {
                IERC20(d.token).safeTransfer(treasury, appealStakes[_disputeId]);
            }
        }

        emit DisputeResolved(_disputeId, d.resolution, claimantPayout, respondentPayout);
    }

    /**
     * @notice Cancel dispute (claimant only, before acceptance)
     */
    function cancelDispute(uint256 _disputeId) 
        external 
        nonReentrant 
        validDispute(_disputeId) 
    {
        Dispute storage d = disputes[_disputeId];
        require(msg.sender == d.claimant, "Only claimant");
        require(d.status == DisputeStatus.Created, "Cannot cancel");

        d.status = DisputeStatus.Cancelled;

        if (d.token == address(0)) {
            _transferETH(d.claimant, d.amount);
        } else {
            IERC20(d.token).safeTransfer(d.claimant, d.amount);
        }

        emit DisputeCancelled(_disputeId);
    }

    function _transferETH(address to, uint256 amount) internal {
        (bool success, ) = to.call{value: amount}("");
        require(success, "ETH transfer failed");
    }

    // ============ View Functions ============

    function getDispute(uint256 _disputeId) external view returns (Dispute memory) {
        return disputes[_disputeId];
    }

    function getDisputeEvidence(uint256 _disputeId) external view returns (Evidence[] memory) {
        return disputeEvidence[_disputeId];
    }

    function getAIVerdict(uint256 _disputeId) external view returns (AIVerdict memory) {
        return aiVerdicts[_disputeId];
    }

    function getUserDisputes(address _user) external view returns (uint256[] memory) {
        return userDisputes[_user];
    }

    function getSupportedTokens() external view returns (address[] memory) {
        return supportedTokens;
    }

    function getTokenConfig(address _token) external view returns (TokenConfig memory) {
        return tokenConfigs[_token];
    }

    function isTokenSupported(address _token) external view returns (bool) {
        return _token == address(0) || tokenConfigs[_token].isSupported;
    }

    // ============ Admin Functions ============

    function setTreasury(address _treasury) external onlyRole(ADMIN_ROLE) {
        require(_treasury != address(0), "Invalid treasury");
        treasury = _treasury;
    }

    function setGenLayerOracle(address _oracle) external onlyRole(ADMIN_ROLE) {
        require(_oracle != address(0), "Invalid oracle");
        _revokeRole(ORACLE_ROLE, genLayerOracle);
        genLayerOracle = _oracle;
        _grantRole(ORACLE_ROLE, _oracle);
    }

    function setEthLimits(uint256 _min, uint256 _max) external onlyRole(ADMIN_ROLE) {
        require(_min < _max, "Invalid limits");
        minEthAmount = _min;
        maxEthAmount = _max;
    }

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function emergencyWithdraw(address _token) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_token == address(0)) {
            _transferETH(treasury, address(this).balance);
        } else {
            uint256 balance = IERC20(_token).balanceOf(address(this));
            IERC20(_token).safeTransfer(treasury, balance);
        }
    }

    receive() external payable {}
}

interface IERC20Metadata {
    function decimals() external view returns (uint8);
}

contracts/ReputationSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ReputationSystem
 * @notice Manages user reputation scores based on dispute outcomes
 * @dev Reputation affects dispute weighting and platform privileges
 */
contract ReputationSystem is AccessControl, ReentrancyGuard {

    // ============ Roles ============
    bytes32 public constant DISPUTE_CONTRACT_ROLE = keccak256("DISPUTE_CONTRACT_ROLE");
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");

    // ============ Structs ============
    struct UserReputation {
        uint256 score;              // Current reputation score (basis points, 10000 = 100%)
        uint256 totalDisputes;      // Total disputes participated in
        uint256 disputesWon;        // Disputes won
        uint256 disputesLost;       // Disputes lost
        uint256 disputesSplit;      // Disputes ended in split
        uint256 evidenceQuality;    // Average evidence quality score
        uint256 responseTime;       // Average response time in seconds
        uint256 appealSuccessRate;  // Successful appeals (basis points)
        uint256 lastActivityAt;     // Last activity timestamp
        uint256 createdAt;          // First activity timestamp
        bool isBanned;              // Ban status
        string banReason;           // Reason for ban
    }

    struct ReputationTier {
        string name;
        uint256 minScore;
        uint256 maxFeeDiscount;     // Fee discount in basis points
        uint256 maxDisputeAmount;   // Maximum dispute amount allowed
        bool canAppeal;             // Can appeal verdicts
        bool prioritySupport;       // Gets priority in queue
    }

    // ============ State Variables ============
    mapping(address => UserReputation) public userReputations;
    mapping(uint256 => ReputationTier) public reputationTiers;
    uint256 public tierCount;

    // Reputation change weights
    uint256 public constant WIN_BONUS = 500;           // +5%
    uint256 public constant LOSS_PENALTY = 300;        // -3%
    uint256 public constant SPLIT_BONUS = 100;         // +1%
    uint256 public constant APPEAL_SUCCESS_BONUS = 800; // +8%
    uint256 public constant APPEAL_FAIL_PENALTY = 400; // -4%
    uint256 public constant HIGH_CONFIDENCE_BONUS = 200; // +2% for high AI confidence
    uint256 public constant EVIDENCE_QUALITY_BONUS = 150; // +1.5% per quality point
    uint256 public constant FAST_RESPONSE_BONUS = 100;  // +1% for fast response

    uint256 public constant BASE_REPUTATION = 5000;    // 50% starting reputation
    uint256 public constant MIN_REPUTATION = 100;      // 1% minimum
    uint256 public constant MAX_REPUTATION = 10000;    // 100% maximum

    uint256 public constant BAN_THRESHOLD = 500;       // Auto-ban below 5%
    uint256 public constant FRAUD_PENALTY = 2000;      // -20% for fraud

    // ============ Events ============
    event ReputationUpdated(
        address indexed user,
        uint256 oldScore,
        uint256 newScore,
        string reason
    );

    event UserBanned(
        address indexed user,
        string reason,
        address bannedBy
    );

    event UserUnbanned(
        address indexed user,
        address unbannedBy
    );

    event TierCreated(
        uint256 indexed tierId,
        string name,
        uint256 minScore
    );

    event EvidenceQualityRated(
        address indexed user,
        uint256 disputeId,
        uint256 qualityScore
    );

    // ============ Constructor ============
    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MODERATOR_ROLE, msg.sender);

        // Initialize default tiers
        _createDefaultTiers();
    }

    // ============ Internal Functions ============

    function _createDefaultTiers() internal {
        // Tier 0: New User
        reputationTiers[0] = ReputationTier({
            name: "New User",
            minScore: 0,
            maxFeeDiscount: 0,
            maxDisputeAmount: 1 ether,
            canAppeal: false,
            prioritySupport: false
        });

        // Tier 1: Bronze
        reputationTiers[1] = ReputationTier({
            name: "Bronze",
            minScore: 3000,
            maxFeeDiscount: 500,  // 5% discount
            maxDisputeAmount: 10 ether,
            canAppeal: true,
            prioritySupport: false
        });

        // Tier 2: Silver
        reputationTiers[2] = ReputationTier({
            name: "Silver",
            minScore: 5000,
            maxFeeDiscount: 1000, // 10% discount
            maxDisputeAmount: 50 ether,
            canAppeal: true,
            prioritySupport: false
        });

        // Tier 3: Gold
        reputationTiers[3] = ReputationTier({
            name: "Gold",
            minScore: 7000,
            maxFeeDiscount: 1500, // 15% discount
            maxDisputeAmount: 200 ether,
            canAppeal: true,
            prioritySupport: true
        });

        // Tier 4: Platinum
        reputationTiers[4] = ReputationTier({
            name: "Platinum",
            minScore: 9000,
            maxFeeDiscount: 2500, // 25% discount
            maxDisputeAmount: 1000 ether,
            canAppeal: true,
            prioritySupport: true
        });

        tierCount = 5;
    }

    function _getOrCreateReputation(address user) internal returns (UserReputation storage) {
        UserReputation storage rep = userReputations[user];
        if (rep.createdAt == 0) {
            rep.score = BASE_REPUTATION;
            rep.createdAt = block.timestamp;
            rep.lastActivityAt = block.timestamp;
        }
        return rep;
    }

    function _adjustScore(uint256 currentScore, int256 change) internal pure returns (uint256) {
        if (change >= 0) {
            uint256 newScore = currentScore + uint256(change);
            return newScore > MAX_REPUTATION ? MAX_REPUTATION : newScore;
        } else {
            uint256 decrease = uint256(-change);
            if (decrease >= currentScore) {
                return MIN_REPUTATION;
            }
            return currentScore - decrease;
        }
    }

    // ============ Core Functions ============

    /**
     * @notice Record a dispute outcome and update reputation
     * @param user Address of the user
     * @param won Whether the user won
     * @param split Whether it was a split decision
     * @param aiConfidence AI confidence score (0-100)
     */
    function recordDisputeOutcome(
        address user,
        bool won,
        bool split,
        uint8 aiConfidence
    ) external onlyRole(DISPUTE_CONTRACT_ROLE) {
        UserReputation storage rep = _getOrCreateReputation(user);
        require(!rep.isBanned, "User is banned");

        uint256 oldScore = rep.score;
        int256 scoreChange = 0;

        rep.totalDisputes++;
        rep.lastActivityAt = block.timestamp;

        if (split) {
            rep.disputesSplit++;
            scoreChange = int256(SPLIT_BONUS);
        } else if (won) {
            rep.disputesWon++;
            scoreChange = int256(WIN_BONUS);

            // Bonus for high AI confidence agreement
            if (aiConfidence >= 85) {
                scoreChange += int256(HIGH_CONFIDENCE_BONUS);
            }
        } else {
            rep.disputesLost++;
            scoreChange = -int256(LOSS_PENALTY);

            // Extra penalty for high AI confidence loss
            if (aiConfidence >= 90) {
                scoreChange -= int256(HIGH_CONFIDENCE_BONUS);
            }
        }

        rep.score = _adjustScore(oldScore, scoreChange);

        // Check for auto-ban
        if (rep.score <= BAN_THRESHOLD) {
            rep.isBanned = true;
            rep.banReason = "Reputation too low";
            emit UserBanned(user, rep.banReason, address(this));
        }

        emit ReputationUpdated(user, oldScore, rep.score, won ? "Won dispute" : (split ? "Split decision" : "Lost dispute"));
    }

    /**
     * @notice Record an appeal outcome
     * @param user Address of the user who appealed
     * @param successful Whether the appeal was successful
     */
    function recordAppealOutcome(
        address user,
        bool successful
    ) external onlyRole(DISPUTE_CONTRACT_ROLE) {
        UserReputation storage rep = _getOrCreateReputation(user);
        require(!rep.isBanned, "User is banned");

        uint256 oldScore = rep.score;
        int256 scoreChange;

        if (successful) {
            scoreChange = int256(APPEAL_SUCCESS_BONUS);
            // Update appeal success rate
            uint256 totalAppeals = rep.totalDisputes > 0 ? rep.totalDisputes / 10 : 1;
            rep.appealSuccessRate = (rep.appealSuccessRate * (totalAppeals - 1) + 10000) / totalAppeals;
        } else {
            scoreChange = -int256(APPEAL_FAIL_PENALTY);
        }

        rep.score = _adjustScore(oldScore, scoreChange);
        rep.lastActivityAt = block.timestamp;

        emit ReputationUpdated(user, oldScore, rep.score, successful ? "Successful appeal" : "Failed appeal");
    }

    /**
     * @notice Rate the quality of evidence submitted by a user
     * @param user Address of the user
     * @param disputeId ID of the dispute
     * @param qualityScore Quality score (0-100)
     */
    function rateEvidenceQuality(
        address user,
        uint256 disputeId,
        uint256 qualityScore
    ) external onlyRole(MODERATOR_ROLE) {
        require(qualityScore <= 100, "Invalid quality score");

        UserReputation storage rep = _getOrCreateReputation(user);

        // Update rolling average
        if (rep.evidenceQuality == 0) {
            rep.evidenceQuality = qualityScore;
        } else {
            rep.evidenceQuality = (rep.evidenceQuality + qualityScore) / 2;
        }

        // Bonus for high quality evidence
        if (qualityScore >= 80) {
            uint256 oldScore = rep.score;
            uint256 bonus = (qualityScore - 80) * EVIDENCE_QUALITY_BONUS / 20;
            rep.score = _adjustScore(oldScore, int256(bonus));
            emit ReputationUpdated(user, oldScore, rep.score, "High quality evidence");
        }

        emit EvidenceQualityRated(user, disputeId, qualityScore);
    }

    /**
     * @notice Record response time for a dispute
     * @param user Address of the user
     * @param responseTimeSeconds Response time in seconds
     */
    function recordResponseTime(
        address user,
        uint256 responseTimeSeconds
    ) external onlyRole(DISPUTE_CONTRACT_ROLE) {
        UserReputation storage rep = _getOrCreateReputation(user);

        // Update rolling average
        if (rep.responseTime == 0) {
            rep.responseTime = responseTimeSeconds;
        } else {
            rep.responseTime = (rep.responseTime + responseTimeSeconds) / 2;
        }

        // Bonus for fast response (under 1 hour)
        if (responseTimeSeconds < 3600) {
            uint256 oldScore = rep.score;
            rep.score = _adjustScore(oldScore, int256(FAST_RESPONSE_BONUS));
            emit ReputationUpdated(user, oldScore, rep.score, "Fast response");
        }
    }

    /**
     * @notice Apply fraud penalty to a user
     * @param user Address of the user
     * @param reason Reason for the penalty
     */
    function applyFraudPenalty(
        address user,
        string calldata reason
    ) external onlyRole(MODERATOR_ROLE) {
        UserReputation storage rep = _getOrCreateReputation(user);

        uint256 oldScore = rep.score;
        rep.score = _adjustScore(oldScore, -int256(FRAUD_PENALTY));

        if (rep.score <= BAN_THRESHOLD) {
            rep.isBanned = true;
            rep.banReason = reason;
            emit UserBanned(user, reason, msg.sender);
        }

        emit ReputationUpdated(user, oldScore, rep.score, reason);
    }

    // ============ Admin Functions ============

    /**
     * @notice Ban a user
     * @param user Address to ban
     * @param reason Reason for ban
     */
    function banUser(
        address user,
        string calldata reason
    ) external onlyRole(MODERATOR_ROLE) {
        UserReputation storage rep = _getOrCreateReputation(user);
        rep.isBanned = true;
        rep.banReason = reason;
        emit UserBanned(user, reason, msg.sender);
    }

    /**
     * @notice Unban a user
     * @param user Address to unban
     */
    function unbanUser(address user) external onlyRole(MODERATOR_ROLE) {
        UserReputation storage rep = userReputations[user];
        require(rep.isBanned, "User not banned");
        rep.isBanned = false;
        rep.banReason = "";
        // Reset to minimum viable reputation
        if (rep.score < BASE_REPUTATION / 2) {
            rep.score = BASE_REPUTATION / 2;
        }
        emit UserUnbanned(user, msg.sender);
    }

    /**
     * @notice Create a new reputation tier
     */
    function createTier(
        string calldata name,
        uint256 minScore,
        uint256 maxFeeDiscount,
        uint256 maxDisputeAmount,
        bool canAppeal,
        bool prioritySupport
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        reputationTiers[tierCount] = ReputationTier({
            name: name,
            minScore: minScore,
            maxFeeDiscount: maxFeeDiscount,
            maxDisputeAmount: maxDisputeAmount,
            canAppeal: canAppeal,
            prioritySupport: prioritySupport
        });

        emit TierCreated(tierCount, name, minScore);
        tierCount++;
    }

    /**
     * @notice Set dispute contract role
     */
    function setDisputeContract(address disputeContract) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(DISPUTE_CONTRACT_ROLE, disputeContract);
    }

    // ============ View Functions ============

    function getReputation(address user) external view returns (UserReputation memory) {
        UserReputation memory rep = userReputations[user];
        if (rep.createdAt == 0) {
            rep.score = BASE_REPUTATION;
        }
        return rep;
    }

    function getReputationScore(address user) external view returns (uint256) {
        UserReputation memory rep = userReputations[user];
        return rep.createdAt == 0 ? BASE_REPUTATION : rep.score;
    }

    function getUserTier(address user) external view returns (uint256 tierId, ReputationTier memory tier) {
        uint256 score = userReputations[user].createdAt == 0 ? BASE_REPUTATION : userReputations[user].score;

        for (uint256 i = tierCount; i > 0; i--) {
            if (score >= reputationTiers[i - 1].minScore) {
                return (i - 1, reputationTiers[i - 1]);
            }
        }
        return (0, reputationTiers[0]);
    }

    function getFeeDiscount(address user) external view returns (uint256) {
        uint256 score = userReputations[user].createdAt == 0 ? BASE_REPUTATION : userReputations[user].score;

        for (uint256 i = tierCount; i > 0; i--) {
            if (score >= reputationTiers[i - 1].minScore) {
                return reputationTiers[i - 1].maxFeeDiscount;
            }
        }
        return 0;
    }

    function canUserAppeal(address user) external view returns (bool) {
        if (userReputations[user].isBanned) return false;

        uint256 score = userReputations[user].createdAt == 0 ? BASE_REPUTATION : userReputations[user].score;

        for (uint256 i = tierCount; i > 0; i--) {
            if (score >= reputationTiers[i - 1].minScore) {
                return reputationTiers[i - 1].canAppeal;
            }
        }
        return false;
    }

    function isUserBanned(address user) external view returns (bool) {
        return userReputations[user].isBanned;
    }

    function getMaxDisputeAmount(address user) external view returns (uint256) {
        if (userReputations[user].isBanned) return 0;

        uint256 score = userReputations[user].createdAt == 0 ? BASE_REPUTATION : userReputations[user].score;

        for (uint256 i = tierCount; i > 0; i--) {
            if (score >= reputationTiers[i - 1].minScore) {
                return reputationTiers[i - 1].maxDisputeAmount;
            }
        }
        return reputationTiers[0].maxDisputeAmount;
    }

    function getTier(uint256 tierId) external view returns (ReputationTier memory) {
        require(tierId < tierCount, "Invalid tier");
        return reputationTiers[tierId];
    }
}

